import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './Autocomplete.css'

function Autocomplete() {
    const [searchValue, setSearchValue] = useState('')
    // const [allStudents, setAllStudents] = useState([])
    const [suggestions, setSuggestions] = useState([])
    // const [suggestionLength, setSuggestionLength] = useState(0)
    const [showSuggestions, setShowSuggestions] = useState(false)

    // useEffect(() => {
    //     axios.get('/students', { params: {
    //         search: searchValue
    //     }})
    //     .then(resp => {setAllStudents(resp.data.students)
    //     console.log(resp)})
    // }, [searchValue])

    const handleSuggestionClick = (e) => {
        setSearchValue(e.target.innerText)
        setShowSuggestions(false)
    }


    let autocompleteSuggestions

    if (showSuggestions && searchValue) {
        if (suggestions.length > 0) {
            autocompleteSuggestions = (
                <ul>
                    {suggestions.map((suggestion, idx) => {
                        return (
                            <li key={`${suggestion.firstName}-${suggestion.id}`} onClick={handleSuggestionClick}>{`${suggestion.firstName} ${suggestion.lastName}`}</li>
                        )
                    })}
                </ul>
            )
        }
        else {
            autocompleteSuggestions = (
                    <p>No Matches</p>
            )
        }
    }

    const handleInputChange = (e) => {
        // console.log(e.target.value)
        // console.log(allStudents[1].firstName)
        let allStudents = []

        axios.get('/students', { params: {
            search: e.target.value
        }})
        .then(resp => {allStudents = resp.data.students
        console.log(allStudents)
        })

        const searchSuggestions = allStudents.sort((a,b) => a.firstName.localeCompare(b.firstName) || a.lastName.localeCompare(b.lastName))

        // const searchSuggestions = allStudents.filter(
        //     suggestion => (
        //         suggestion.firstName.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1 || suggestion.lastName.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1
        //     )
        // )
        setSearchValue(e.target.value)
        setSuggestions(searchSuggestions)
        console.log({'search suggestions': searchSuggestions})
        setShowSuggestions(true)
    }

    return (
        <div className="search-box">
            <h1 className="title">Student Search</h1>
            <input className='search-bar' type="search" value={searchValue} onChange={handleInputChange}></input>
            {autocompleteSuggestions}
            {searchValue}
        </div>
    )
}

export default Autocomplete
