import React, { useEffect, useState } from 'react'
import axios from 'axios'
import './Autocomplete.css'

function Autocomplete() {
    const [searchValue, setSearchValue] = useState('')
    const [allStudents, setAllStudents] = useState([])
    const [suggestions, setSuggestions] = useState([])
    // const [suggestionLength, setSuggestionLength] = useState(0)
    const [showSuggestions, setShowSuggestions] = useState(false)
    const [apiDone, setApiDone] = useState(false)
    const [searchCleared, setSearchCleared] = useState(false)

    const handleSuggestionClick = (e) => {
        setSearchValue(e.target.innerText)
        setShowSuggestions(false)
    }

    let autocompleteSuggestions

    if (showSuggestions && searchValue) {
        if (suggestions.length > 0) {
            autocompleteSuggestions = (
                <ul>
                    {suggestions.map((suggestion, idx) => {
                        const firstName = suggestion.firstName
                        const lastName = suggestion.lastName
                        if(firstName.toLowerCase().indexOf(searchValue.toLowerCase()) === 0) {
                            const fullName = firstName.concat(" ", lastName)
                            return (
                                <li className="suggestion" key={`${suggestion.firstName}-${suggestion.id}`} onClick={handleSuggestionClick}>
                                    <b>{firstName.slice(0, searchValue.length)}</b>{fullName.slice(searchValue.length)} {lastName}
                                </li>
                            )
                        }
                        else {
                            return (
                                <li className="suggestion" key={`${suggestion.firstName}-${suggestion.id}`} onClick={handleSuggestionClick}>
                                    {firstName} <b>{lastName.slice(0, searchValue.length)}</b>{lastName.slice(searchValue.length)}
                                </li>
                            )
                        }
                    })}
                </ul>
            )
        }
        else {
            autocompleteSuggestions = (
                    <p>No Matches</p>
            )
        }
    }

    const handleInputChange = (e) => {
        console.log("start")
        setApiDone(false)
        if(e.target.value !== "") {
            setSearchCleared(false)

            axios.get('/students', { params: {
                search: e.target.value
            }})
            .then(resp => {
            const allStudents = resp.data.students
            console.log(allStudents)

            setAllStudents(allStudents)

            setSuggestions(allStudents)
            setApiDone(true)
            setShowSuggestions(true)
            })
        } else setSearchCleared(true)

        setSearchValue(e.target.value)

    }

    return (
        <div className="search-box">
            <h1 className="title">Student Search</h1>
            <input className='search-bar' type="search" value={searchValue} onChange={handleInputChange} placeholder="Search"></input>
            {!searchCleared && autocompleteSuggestions}
        </div>
    )
}

export default Autocomplete
