import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Autocomplete() {
    const [searchValue, setSearchValue] = useState('')
    const [allStudents, setAllStudents] = useState([])
    const [suggestions, setSuggestions] = useState([])
    const [suggestionLength, setSuggestionLength] = useState(0)
    const [showSuggestions, setShowSuggestions] = useState(false)

    useEffect(() => {
        axios.get('/students')
        .then(resp => setAllStudents(resp.data.test2))
    }, [])

    const handleSuggestionClick = (e) => {
        setSearchValue(e.target.innerText)
        setShowSuggestions(false)
    }


    let autocompleteSuggestions

    if (showSuggestions && searchValue) {
        if (suggestions) {
            autocompleteSuggestions = (
                <ul>
                    {suggestions.map((suggestion, idx) => {
                        return (
                            <li key={suggestion} onClick={handleSuggestionClick}>{suggestion}</li>
                        )
                    })}
                </ul>
            )
        }
        else {
            autocompleteSuggestions = (
                <div>
                    <a>No Match</a>
                </div>
            )
        }
    }
    // const getSuggestions = suggestions.map((suggestion, idx) => {
    //     if (showSuggestions && searchValue) {
    //         if (suggestions) {
    //             console.log(suggestions)
    //             console.log("showing suggestions")
    //             return <li key={suggestion} onClick={handleSuggestionClick}>{suggestion}</li> 
    //         }
    //         else {
    //             console.log(suggestions)
    //             console.log("not showing sug")
    //             return (
    //                 <div>
    //                     <p>no match</p>
    //                 </div>
    //             )
    //         }
    //     }
    // })

    const handleInputChange = (e) => {
        console.log(e.target.value)
        const searchSuggestions = allStudents.filter(
            suggestion => suggestion.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1
        )
        setSearchValue(e.target.value)
        setSuggestions(searchSuggestions)
        setShowSuggestions(true)
    }

    return (
        <div>
            <input type="search" value={searchValue} onChange={handleInputChange}></input>
            {autocompleteSuggestions}
            {searchValue}
        </div>
    )
}

export default Autocomplete
