import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Autocomplete() {
    const [searchValue, setSearchValue] = useState('')
    const [allStudents, setAllStudents] = useState([])
    const [suggestions, setSuggestions] = useState([])
    const [suggestionLength, setSuggestionLength] = useState(0)
    const [showSuggestions, setShowSuggestions] = useState(false)

    useEffect(() => {
        axios.get('/students')
        .then(resp => setAllStudents(resp.data.test2))
    }, [])

    const handleSuggestionClick = (e) => {
        setSearchValue(e.target.innerText)
    }

    const getSuggestions = suggestions.map((suggestion, idx) => {
        if (searchValue.length > 0) {
            if (suggestionLength < 5) {
                setSuggestionLength(suggestionLength + 1)
                console.log(suggestionLength)
                return <ul key={suggestion} onClick={handleSuggestionClick}>{suggestion}</ul> 
            }
        }
    })

    const handleInputChange = (e) => {
        console.log(e.target.value)
        const searchSuggestions = allStudents.filter(
            suggestion => suggestion.toLowerCase().indexOf(e.target.value.toLowerCase()) > -1
        )
        setSearchValue(e.target.value)
        setSuggestions(searchSuggestions)
    }

    return (
        <div>
            <input type="search" value={searchValue} onChange={handleInputChange}></input>
            {getSuggestions}
            {searchValue}
        </div>
    )
}

export default Autocomplete
