import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Autocomplete(props) {
    const [value, setValue] = useState('')
    const [suggestions, setSuggestions] = useState([])

    useEffect(() => {
        axios.get('/students')
        .then( resp => setSuggestions(resp.data.test2) )
        console.log(suggestions)
    }, [])

    const handleSuggestionClick = () => {
        console.log('clicked')
    }

    const getSuggestions = suggestions.map((suggestion, idx) => {
        return <ul key={suggestion} onClick={handleSuggestionClick}>{suggestion}</ul>
    })

    const handleInputChange = (e) => {
        setValue(e.target.value)
        console.log(value)
    }



    // onSubmit = () => {
    //     null
    // }



    return (
        <div>
            <h1>HI</h1>
            <input type="search" value={value} onChange={handleInputChange}></input>
            {getSuggestions}
        </div>
    )
}

export default Autocomplete
