import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Autocomplete() {
    const [searchValue, setSearchValue] = useState('')
    const [allStudents, setAllStudents] = useState([])
    const [suggestions, setSuggestions] = useState([])
    const [showSuggestions, setShowSuggestions] = useState(false)

    useEffect(() => {

        axios.get('/students')
        .then(resp => {
            setAllStudents(resp.data.test2)
        })
        console.log(allStudents)
    }, [])

    const handleSuggestionClick = (e) => {
        setValue(e.target.innerText)
    }

    const getSuggestions = suggestions.map((suggestion, idx) => {
        return <ul key={suggestion} onClick={handleSuggestionClick}>{suggestion}</ul>
    })

    const handleInputChange = (e) => {
        setSearchValue(e.target.value)
        if(searchValue.length > 0) {
            setShowSuggestions(true)
        }
    }

    return (
        <div>
            <h1>HI</h1>
            <input type="search" value={searchValue} onChange={handleInputChange}></input>
            {getSuggestions}
        </div>
    )
}

export default Autocomplete
